const initialState = ({
    queries: []
})

export const savedQueries = (state = initialState, action) => {
    switch (action.type) {
        case 'addQuery':
            return {
                ...state,
                queries: state.queries.concat(action.name)
            }
        case 'removeQuery':
            const index = state.queries.indexOf(action.name);
            state.queries.splice(index, 1);
