import { changedQuery } from "../Actions/action";

export const removeQuery = (name) => (dispatch, getState) =>{
    const copy = getState().savedQueries
    const index = copy.queries.indexOf(name);
    copy.queries.splice(index, 1);
    console.log(copy)
    dispatch(changedQuery(copy.queries));
}
