const initialState = ({
    queries: []
})

export const savedQueries = (state = initialState, action) => {
    switch (action.type) {
        case 'addQuery':
            return {
                ...state,
                queries: state.queries.concat(action.name)
            }
        case 'removeQuery':
            return {
                ...state,
                queries: state.queries.splice(state.queries.indexOf(action.name), 1)
            }
        default: return state
    }
}
