import React from "react";
import './search.style.scss';
import { connect } from "react-redux";

class Search extends React.Component {
  constructor() {
    super();

    this.state = {
      searchText: ""
    };
  }

  setSearchText = e => {
    this.setState({ searchText: e.target.value });
  };

  render() {
    return (
      <div className="search">
        <input type="text" placeholder="Search for images" onChange={e => this.setSearchText(e)} />
        <button onClick={() => this.props.search(this.state.searchText)}>Search</button>
        <button onClick={() => this.props.onAddQuery(this.state.searchText)}>Save</button>
      </div>
    );
  }
}

const mapDispatchToProps = dispatch => ({
  onAddQuery: name => dispatch(addQuery(name))
})

export default connect(null, map)(Search);
